(function() {
    try {
        const keyName = 'petDevMode';
        const defaultEnabled = false;
        const original = {
            log: console.log,
            info: console.info,
            debug: console.debug,
            warn: console.warn
        };
        const muteIfNeeded = (enabled) => {
            if (enabled) return;
            const noop = () => {};
            console.log = noop;
            console.info = noop;
            console.debug = noop;
            console.warn = noop;
        };
        chrome.storage.sync.get([keyName], (res) => {
            const enabled = res[keyName];
            muteIfNeeded(typeof enabled === 'boolean' ? enabled : defaultEnabled);
        });
        chrome.storage.onChanged.addListener((changes, namespace) => {
            if (namespace !== 'sync') return;
            if (changes[keyName]) {
                const enabled = changes[keyName].newValue;
                if (enabled) {
                    console.log = original.log;
                    console.info = original.info;
                    console.debug = original.debug;
                    console.warn = original.warn;
                } else {
                    const noop = () => {};
                    console.log = noop;
                    console.info = noop;
                    console.debug = noop;
                    console.warn = noop;
                }
            }
        });
    } catch (e) {}
})();

