        // æŒ‰æ›´æ–°æ—¶é—´æ’åºä¼šè¯ï¼ˆæœ€æ–°æ›´æ–°çš„åœ¨å‰ï¼Œä¸æ¥å£æ’åºä¸€è‡´ï¼‰
        // è¿™æ ·å¯ä»¥ç¡®ä¿åˆ—è¡¨ä¸æ¥å£è¿”å›çš„é¡ºåºä¸€è‡´
        const sortedSessions = allSessions.sort((a, b) => {
            const aUpdated = a.updatedAt || a.createdAt || 0;
            const bUpdated = b.updatedAt || b.createdAt || 0;
            
            // é¦–å…ˆæŒ‰æ›´æ–°æ—¶é—´æ’åºï¼ˆæœ€æ–°æ›´æ–°çš„åœ¨å‰ï¼‰
            if (aUpdated !== bUpdated) {
                return bUpdated - aUpdated;
            }
            
            // å¦‚æœæ›´æ–°æ—¶é—´ç›¸åŒï¼ŒæŒ‰ä¼šè¯IDæ’åºï¼ˆç¡®ä¿å®Œå…¨ç¨³å®šï¼‰
            const aId = a.id || '';
            const bId = b.id || '';
            return aId.localeCompare(bId);
        });
        
