            let updatedAt = session.updatedAt || Date.now();
            
            // å°†æ—¶é—´æˆ³è½¬æ¢ä¸º datetime-local æ ¼å¼ (YYYY-MM-DDTHH:mm)
            const date = new Date(updatedAt);
            const year = date.getFullYear();
            const month = String(date.getMonth() + 1).padStart(2, '0');
