                    // è·å–è§’è‰²ä¿¡æ¯
                    const pageInfo = this.getPageInfo(); // ä¿ç•™ç”¨äºè·å–è§’è‰²é…ç½®ï¼Œä½†ä¸ç”¨äº userPrompt
                    let roleInfo;
