    // 查找与OSS文件关联的所有会话
    // @param {string} objectName - 文件对象名
    // @returns {Array<string>} 关联的会话ID数组
    _findSessionsByOssFile(objectName) {
        if (!objectName) {
            return [];
        }
        
        const matchedSessionIds = [];
        
        // 获取文件对象（用于匹配）
        let fileObject = null;
        let fileUrl = null;
        if (this.ossFileManager) {
            const allFiles = this.ossFileManager.getAllFiles();
            fileObject = allFiles.find(file => file.name === objectName);
            if (fileObject) {
                fileUrl = fileObject.url;
            }
        }
        
        // 遍历所有会话，找到关联的会话
        for (const sessionId in this.sessions) {
            const session = this.sessions[sessionId];
            if (session && session._isOssFileSession && session._ossFileInfo) {
                // 优先使用 URL 匹配（最可靠）
                let isMatch = false;
                if (fileUrl && session._ossFileInfo.url) {
                    isMatch = session._ossFileInfo.url === fileUrl;
                } else {
                    // 如果 URL 不可用，使用原始文件名匹配
                    const sessionUrl = session._ossFileInfo.url || '';
                    const sessionName = session._ossFileInfo.name || '';
                    // 检查 URL 是否包含文件名，或者原始名称是否匹配
                    isMatch = sessionUrl.includes(objectName) || 
                             (sessionName === objectName) ||
                             (fileObject && sessionUrl === fileObject.url);
                }
                
                if (isMatch) {
                    matchedSessionIds.push(sessionId);
                }
            }
        }
        
        return matchedSessionIds;
    }

    // 上传文件到OSS
    async uploadFileToOss() {
        if (!this.ossApi || !this.ossApi.isEnabled()) {
            this.showNotification('OSS API未启用', 'error');
            return;
        }

        // 创建文件选择器
        const fileInput = document.createElement('input');
        fileInput.type = 'file';
        fileInput.multiple = true; // 支持多文件上传
        fileInput.style.display = 'none';

        fileInput.addEventListener('change', async (event) => {
            const files = Array.from(event.target.files);
            if (!files || files.length === 0) {
                // 用户取消选择
                if (fileInput.parentNode) {
                    fileInput.parentNode.removeChild(fileInput);
                }
                return;
            }

            try {
                // 获取当前目录
                const directory = this.currentOssDirectory || '';

                // 显示上传中提示
                if (files.length === 1) {
                    this.showNotification('正在上传文件...', 'info');
                } else {
                    this.showNotification(`正在上传 ${files.length} 个文件...`, 'info');
                }

                let successCount = 0;
                let failCount = 0;
                const errors = [];

                // 逐个上传文件
                for (let i = 0; i < files.length; i++) {
                    const file = files[i];
                    try {
                        // 上传文件
                        const result = await this.ossApi.uploadFile(file, directory);

                        if (result && result.code === 200) {
                            successCount++;
                        } else {
                            failCount++;
                            errors.push(`${file.name}: ${result?.message || '上传失败'}`);
                        }
                    } catch (error) {
                        console.error(`上传文件 ${file.name} 失败:`, error);
                        failCount++;
                        errors.push(`${file.name}: ${error.message || '未知错误'}`);
                    }
                }

                // 显示上传结果
                if (successCount > 0 && failCount === 0) {
                    // 全部成功
                    if (files.length === 1) {
                        this.showNotification('文件上传成功', 'success');
                    } else {
                        this.showNotification(`成功上传 ${successCount} 个文件`, 'success');
                    }
                } else if (successCount > 0 && failCount > 0) {
                    // 部分成功
                    this.showNotification(`成功上传 ${successCount} 个文件，${failCount} 个失败`, 'error');
                    console.warn('上传失败的文件:', errors);
                } else {
                    // 全部失败
                    this.showNotification(`上传失败: ${errors[0] || '未知错误'}`, 'error');
                    if (errors.length > 1) {
                        console.error('所有文件上传失败:', errors);
                    }
                }
                
                // 如果有成功上传的文件，刷新文件列表
                if (successCount > 0) {
                    if (this.ossFileManager) {
                        await this.ossFileManager.refreshFiles(true);
                    }
                    await this.updateOssFileSidebar(true);
                }
            } catch (error) {
                console.error('上传文件失败:', error);
                this.showNotification('上传文件失败: ' + (error.message || '未知错误'), 'error');
            } finally {
                // 清理文件选择器
                fileInput.value = '';
                if (fileInput.parentNode) {
                    fileInput.parentNode.removeChild(fileInput);
                }
            }
        });

        // 添加到页面并触发点击
        document.body.appendChild(fileInput);
        fileInput.click();
    }

