                    '.png': '🖼️',
                    '.gif': '🖼️',
                    '.webp': '🖼️',
                    '.bmp': '🖼️',
                    '.pdf': '📄',
                    '.doc': '📝',
                    '.docx': '📝',
                    '.zip': '📦',
                    '.rar': '📦',
                    '.7z': '📦',
