    // 提取API路径（从完整URL中提取路径部分）
    _extractApiPath(url) {
        if (!url) return '';
        try {
            const urlObj = new URL(url);
            return urlObj.pathname + urlObj.search;
        } catch (e) {
            // 如果URL解析失败，尝试简单提取
            const match = url.match(/\/\/[^\/]+(\/.*)/);
            return match ? match[1] : url;
        }
    }
    
